// src/components/TopNavbar.jsx
import React, { useEffect, useRef, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Link, useLocation, useNavigate } from "react-router-dom";
import {
  FaWallet,
  FaHistory,
  FaCog,
  FaSignOutAlt,
  FaBars,
  FaTimes,
  FaArrowRight,
  FaSignInAlt,
  FaHeadset, // Support icon ke liye
} from "react-icons/fa";
import { useAuth } from "../context/AuthContext";

export default function TopNavbar() {
  const [showDropdown, setShowDropdown] = useState(false);
  const [mobileOpen, setMobileOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const panelRef = useRef(null);

  const { isLoggedIn, logoutUser } = useAuth();

  useEffect(() => {
    setMobileOpen(false);
    setShowDropdown(false);
  }, [location.pathname]);

  useEffect(() => {
    const onKey = (e) => {
      if (e.key === "Escape") {
        setMobileOpen(false);
        setShowDropdown(false);
      }
    };
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, []);

  // ✅ LOGO COMPONENT
  const Logo1CTrader = ({ className = "" }) => (
    <svg className={`w-[184px] h-auto ${className}`} viewBox="0 0 300 80" fill="none" xmlns="http://www.w3.org/2000/svg">
      <defs>
        <linearGradient id="gold" x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" stopColor="#f59e0b" />
          <stop offset="50%" stopColor="#eab308" />
          <stop offset="100%" stopColor="#d97706" />
        </linearGradient>
      </defs>
      <text x="50" y="44" fontSize="34" fontWeight="900" fill="#fff" stroke="#f59e0b" strokeWidth="1.2">1C</text>
      <text x="110" y="48" fontSize="25" fontWeight="700" fill="url(#gold)" letterSpacing="0.4">Global</text>
      <path d="M25 55 L45 35 L65 48 L85 28 L105 42" stroke="url(#gold)" strokeWidth="2.5" fill="none" />
    </svg>
  );

  const navLinks = [
    { to: "/about", label: "About Us" },
    { to: "/plans", label: "Investment Plans" },
    { to: "/contact", label: "Contact Us" },
  ];

  const isActive = (to) => location.pathname === to;

  return (
    <>
      <motion.nav
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        className="fixed top-0 left-0 right-0 h-16 bg-gradient-to-r from-black/90 via-slate-900/90 to-black/90 backdrop-blur-xl border-b border-yellow-500/20 z-50"
      >
        <div className="max-w-7xl mx-auto px-4 h-full flex items-center justify-between">
          {/* LEFT */}
          <div className="flex items-center gap-8">
            <Link to="/" className="flex items-center gap-3">
              <Logo1CTrader className="scale-90 opacity-95 hover:opacity-100 transition" />
            </Link>

            {/* ✅ DESKTOP LINKS - Only show if NOT logged in */}
            {!isLoggedIn && (
              <div className="hidden md:flex items-center gap-6 text-sm font-medium">
                {navLinks.map((l) => (
                  <Link
                    key={l.to}
                    to={l.to}
                    className={`transition ${isActive(l.to) ? "text-yellow-400" : "text-gray-300 hover:text-yellow-400"}`}
                  >
                    {l.label}
                  </Link>
                ))}
              </div>
            )}
          </div>

          {/* RIGHT */}
          <div className="flex items-center gap-3">
            {!isLoggedIn ? (
              <motion.button
                onClick={() => navigate("/login")}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                className="hidden md:flex items-center gap-2 px-4 h-11 rounded-2xl bg-yellow-500/10 border border-yellow-500/25 text-yellow-300 font-black text-sm hover:bg-yellow-500/15 transition"
              >
                <FaSignInAlt /> Login
              </motion.button>
            ) : (
              <div className="flex items-center gap-2">
                {/* ✅ SUPPORT BUTTON - Visible when logged in */}
                <motion.button
                  onClick={() => navigate("/contact")}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className="flex items-center justify-center w-11 h-11 rounded-2xl bg-white/5 border border-white/10 text-gray-300 hover:text-yellow-400 hover:border-yellow-500/40 transition-all"
                  title="Help & Support"
                >
                  <FaHeadset className="text-lg" />
                </motion.button>

                {/* LOGOUT BUTTON */}
                <motion.button
                  onClick={logoutUser}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  className="hidden md:flex items-center gap-2 px-4 h-11 rounded-2xl bg-red-500/10 border border-red-500/25 text-red-300 font-black text-sm hover:bg-red-500/15 transition"
                >
                  <FaSignOutAlt /> Logout
                </motion.button>
              </div>
            )}

            {/* MOBILE MENU TOGGLE */}
            <motion.button
              onClick={() => setMobileOpen((p) => !p)}
              className="md:hidden w-11 h-11 rounded-2xl bg-black/40 border border-gray-800/70 flex items-center justify-center text-gray-200"
            >
              {mobileOpen ? <FaTimes /> : <FaBars />}
            </motion.button>
          </div>
        </div>
      </motion.nav>

      {/* MOBILE MENU */}
      <AnimatePresence>
        {mobileOpen && (
          <>
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setMobileOpen(false)}
              className="fixed inset-0 z-40 bg-black/60 backdrop-blur-sm md:hidden"
            />
            <motion.aside
              initial={{ x: "110%" }}
              animate={{ x: 0 }}
              exit={{ x: "110%" }}
              className="fixed top-0 right-0 z-50 md:hidden w-[80%] max-w-sm h-full bg-slate-950 border-l border-yellow-500/20 p-6 flex flex-col gap-6"
            >
              {/* Mobile Navigation - Only show if NOT logged in */}
              {!isLoggedIn && (
                <div className="flex flex-col gap-4">
                   <p className="text-[10px] uppercase tracking-widest text-gray-500 font-bold">Menu</p>
                  {navLinks.map((l) => (
                    <Link key={l.to} to={l.to} className="text-lg font-bold text-gray-200">{l.label}</Link>
                  ))}
                </div>
              )}

              {/* Mobile Actions */}
              <div className="mt-auto flex flex-col gap-3">
                {isLoggedIn && (
                  <button 
                    onClick={() => navigate("/contact")}
                    className="flex items-center justify-center gap-2 w-full py-4 rounded-2xl bg-white/5 border border-white/10 text-white font-bold"
                  >
                    <FaHeadset /> Support Center
                  </button>
                )}
                
                {!isLoggedIn ? (
                  <button onClick={() => navigate("/login")} className="w-full py-4 rounded-2xl bg-yellow-500 text-black font-black">Login</button>
                ) : (
                  <button onClick={logoutUser} className="w-full py-4 rounded-2xl bg-red-500/20 border border-red-500/40 text-red-400 font-bold">Logout</button>
                )}
              </div>
            </motion.aside>
          </>
        )}
      </AnimatePresence>
    </>
  );
}
